    def get_entry_texts(self):
        entries = [{'role': 'system', 'content':self.systementry.toPlainText()}]
       
        for i in range(len(self.Uentries)):
            entry = {'role': 'user', 'content': self.Uentries[i].toPlainText()}
            entries.append(entry)
           
            entry2 = {'role': 'user', 'content': self.Bentries[i].toPlainText()}
            entries.append(entry2)
        
        import json
        if self.contextName.text()!="":
            print(entries)
            Title = "BetsyApp/contexts/" +self.contextName.text()+ ".json"
            with open(Title, "w") as f:
                json.dump(entries, f) 
           
        else :
            print("No Name")



